@keyframes fade-in{
  from {
    visibility: hidden;
    opacity: 0;
  }

  1% {
    visibility: visible;
    opacity: 0;

  }
  to {
    visibility: visible;
    opacity: 1;
  }
}

.fade-in {
  animation: fade-in 350ms ease-in-out forwards;
}

@keyframes fade-out {
  from {
    visibility: visible;
    opacity: 1;
    display: block;
  }

  99% {
    visibility: visible;
    opacity: 0;
    display: block;
  }

  to {
    visibility: hidden;
    opacity: 0;
    display: none;
  }
}

.fade-out {
  animation: fade-out 350ms ease-in-out forwards;
}

