.indicator {

  div {
    display: inline-block;
    width: 10px;
    height: 10px;
    border: 1px solid orangered;
    border-radius: 50%;
    content: '';
    margin-bottom: 1.3rem;
    transition: 200ms background-color ease-in-out;

    &.iActive {
      background-color: orangered;
    }
  }
}

.carousel-btn-left {
  display: table-cell;
  cursor: pointer;
  opacity: 0;
  width: 65px;
  position: relative;
  background-color: rgb(196, 196, 196);
  transition: 200ms opacity ease-in-out;

  &:hover {
    opacity: 0.35;
  }
}

.carousel-btn-right {
  display: table-cell;
  cursor: pointer;
  opacity: 0;
  width: 65px;
  position: relative;
  background-color: rgb(196, 196, 196);;
  transition: 200ms opacity ease-in-out;

  &:hover {
    opacity: 0.35;
  }
}

.btn-icon-left {
  display: block;
  position: absolute;
  height: 100%;

  div {
    position: relative;
    height: 6px;
    width: 40px;
    border-radius: 15px;
    background-color: rgb(134, 134, 134);

    &:first-child {
      top: 43%;
      left: 9px;
      transform: rotate(-45deg);
    }
    &:last-child {
      top: 49%;
      left: 9px;
      transform: rotate(45deg);
    }
  }
}

.btn-icon-right {
  display: block;
  position: absolute;
  height: 100%;

  div {
    position: relative;
    height: 6px;
    width: 40px;
    border-radius: 15px;
    background-color: rgb(134, 134, 134);

    &:first-child {
      top: 43%;
      left: 13px;
      transform: rotate(45deg);
    }
    &:last-child {
      top: 49%;
      left: 13px;
      transform: rotate(-45deg);
    }
  }
}

